fn filter_body_inject_html_append_gzip(c: &mut Criterion) {
    let config = RouterConfig::default();
    let rule = Rule::from_json(
        r#"{
        "id": "1",
        "source": {
            "path": "/source"
        },
        "rank": 1,
        "body_filters": [
            {
                "action": "append_child",
                "value": "<h1>Yolo</h1>",
                "element_tree": ["html", "body"],
                "target_hash": "body::html.head.description",
                "id": "123"
            }
        ]
    }"#,
    )
    .unwrap()
    .into_route(&config);
    let request = Request::from_config(&config, "/".to_string(), None, None, None, None, None);
    let action = if let (Some(action), _, _, _) = Action::from_route_rule(Arc::new(rule), &request) {
        action
    } else {
        panic!("Cannot create action");
    };

    let gzip_small_body = {
        let mut e = flate2::write::GzEncoder::new(Vec::new(), flate2::Compression::default());
        e.write_all(STATIC_SMALL_BODY.as_bytes()).unwrap();
        e.finish().unwrap()
    };
    let gzip_big_body = {
        let mut e = flate2::write::GzEncoder::new(Vec::new(), flate2::Compression::default());
        e.write_all(STATIC_BIG_BODY.as_bytes()).unwrap();
        e.finish().unwrap()
    };

    let mut group = c.benchmark_group("filter_body_inject_html");
    group.sample_size(10);
    group.bench_function(BenchmarkId::from_parameter("filter_body_inject_html_append_gzip_small"), |b| {
        b.iter(|| {
            let mut cloned_action = action.clone();
            if let Some(mut body) = cloned_action.create_filter_body(
                200,
                &[Header {
                    name: "Content-Encoding".to_string(),
                    value: "gzip".to_string(),
                }],
            ) {
                body.filter(gzip_small_body.clone(), None);
                body.end(None);
            } else {
                panic!("Cannot create filter body");
            }
        });
    });
    group.bench_function(BenchmarkId::from_parameter("filter_body_inject_html_append_gzip_big"), |b| {
        b.iter(|| {
            let mut cloned_action = action.clone();
            if let Some(mut body) = cloned_action.create_filter_body(
                200,
                &[Header {
                    name: "Content-Encoding".to_string(),
                    value: "gzip".to_string(),
                }],
            ) {
                body.filter(gzip_big_body.clone(), None);
                body.end(None);
            } else {
                panic!("Cannot create filter body");
            }
        });
    });

    group.finish();
}

